var tyOptions = {
    monthFormat: [, "January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"],
    noThumbnail: "http://3.bp.blogspot.com/-Yw8BIuvwoSQ/VsjkCIMoltI/AAAAAAAAC4c/s55PW6xEKn0/s1600-r/nth.png",
    postperPage: 6,
    labelResults: 6,
    rTitleText: "Recent Posts",
    followByEmailText: "Đăng ký nhận bài viết mới từ blog.",
    shareText: "Share:",
    relatedTitleText: "Cùng chuyên mục",
    cmmTitleText: "Post a Comment"
};

// Main Scripts - "NOTE" (Encrypt in free version)
$(document).ready(function($) {
    $("#menu").each(function() {
        var t = $("#menu .LinkList .widget-content ul").children("li").children("a");
        var l = t.length;
        var a, p, j, f, k;
        for (var i = 0; i < l; i++) {
            a = t.eq(i);
            j = a.text();
            if (j.charAt(0) !== "_") {
                p = t.eq(i + 1);
                f = p.text();
                if (f.charAt(0) === "_") {
                    k = a.parent();
                    k.append('<ul class="sub-menu"/>');
                }
            }
            if (j.charAt(0) === "_") {
                a.text(j.replace("_", ""));
                a.parent().appendTo(k.children(".sub-menu"));
            }
        }
        $("#menu ul li ul").parent("li").addClass("has-sub");
        $("#menu > .widget").css("display", "block");
        $(".mobile-menu").prepend("<div class='nav-toggle'/>");
        $("#nav2").clone().appendTo(".mobile-menu");
        $(".mobile-menu ul li a").on("click", function($this) {
            if ($(this).parent().hasClass('has-sub')) {
                $this.preventDefault();
                if (!$(this).parent().hasClass('show')) {
                    $(this).parent().addClass('show').children('.sub-menu').slideToggle(200);
                } else {
                    $(this).parent().removeClass('show').find('> .sub-menu').slideToggle(200);
                }
            }
        });
        $(".nav-toggle").on("click", function() {
            $("body").toggleClass("show-menu");
        });
        $(".mobile-menu ul li a").each(function() {
            var txta = $(this).attr("href");
            var mtc = txta.match(/[^[\]]+(?=])/g);
            if (txta.charAt(0) === "[") {
                var txt = mtc[0].toLowerCase();
                var sp = mtc[0].split(",");
                var label = sp[0];
                var url = "/search/label/" + label + "?&max-results=" + tyOptions.labelResults;
                if (txt.match("mega")) {
                    $(this).attr("href", url);
                }
            }
        });
    });
    $("#search-icon").on("click", function() {
        $("body").toggleClass("show-search");
    });
    $(".recent-title > h2").text(tyOptions.rTitleText);
    $(".ty-share-post > span").text(tyOptions.shareText);
    $("#related-wrap .title-wrap h2").text(tyOptions.relatedTitleText);
    $(".FollowByEmail .widget-content").prepend("<p class='before-text'>" + tyOptions.followByEmailText + "</p>");
    $(".cmm-title h2").text(tyOptions.cmmTitleText);
    $(".block-image .thumb img, .PopularPosts ul li img, .avatar-image-container img").each(function() {
        var src = $(this).attr("src"),
            src = src.replace(/\/s[0-9]+\-c/g, '/s1600'),
            src = src.replace('/w72-h72-p-nu/', '/s1600/'),
            src = src.replace('/hqdefault.jpg', '/mqdefault.jpg'),
            src = src.replace('/default.jpg', '/mqdefault.jpg'),
            src = src.replace('/s35/', '/s45/'),
            src = src.replace('//img1.blogblog.com/img/blank.gif', '//4.bp.blogspot.com/-uCjYgVFIh70/VuOLn-mL7PI/AAAAAAAADUs/Kcu9wJbv790hIo83rI_s7lLW3zkLY01EA/s45-r/avatar.png'),
            src = src.replace('http://3.bp.blogspot.com/-Yw8BIuvwoSQ/VsjkCIMoltI/AAAAAAAAC4c/s55PW6xEKn0/s1600-r/nth.png', tyOptions.noThumbnail);
        $(this).attr("src", src);
    });
    $(".PopularPosts .widget-content ul li").each(function() {
        var $this = $(this),
            getPost = $this.find(".item-title a"),
            postURL = getPost.attr("href");
        $.ajax({
            url: postURL,
            type: "get",
            success: function(data) {
                var time = $(data).find(".published").text();
                getPost.parent().after("<div class='ty-meta'><span class='item-date'>" + time + "</span></div>");
            }
        });
    });
    $(".Label a").attr("href", function($this, href) {
        return href.replace(href, href + "?&max-results=" + tyOptions.labelResults);
    });

    $("#menu li").each(function() {
        var liContent = $(this);
        var txta = liContent.find("a").attr("href");
        var mtc = txta.match(/[^[\]]+(?=])/g);
        if (txta.charAt(0) === "[") {
            var $this = liContent;
            var txt = mtc[0].toLowerCase();
            var sp = mtc[0].split(",");
            var label = sp[0];
            tyPosts($this, txt, 4, label);
        }
    });
    $("#feat-sec .HTML").each(function() {
        var widget = $(this);
        var widgetContent = widget.children(".widget-content");
        var widgetContentText = widgetContent.text().trim();
        var mtc = widgetContentText.match(/[^[\]]+(?=])/g);
        if (widgetContentText.charAt(0) === "[") {
            var $this = widgetContent;
